mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.ID_ desc

mapper.sql:
from HEALTH_DIETARY_ITEM${tableSuffix} E


	1 = 1


		and E.TENANTID_ = #{tenantId}



		and E.DIETARYID_ = #{dietaryId}



		and E.DIETARYID_ IN

			#{x_dietaryId}




		and E.TEMPLATEID_ = #{templateId}



		and E.TEMPLATEID_ IN

			#{x_templateId}




		and E.TYPEID_ = #{typeId}



		and E.TYPEID_ IN

			#{x_typeId}




		and E.NAME_ = #{name}



		and E.NAME_ like #{nameLike}



		and E.DESCRIPTION_ like #{descriptionLike}



		and E.FOODID_ = #{foodId}



		and E.FOODID_ IN

			#{x_foodId}




		and E.FOODNAME_ like #{foodNameLike}



		and E.QUANTITY_ >= #{quantityGreaterThanOrEqual}



		and E.QUANTITY_ <= #{quantityLessThanOrEqual}



		and E.FULLDAY_ = #{fullDay}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}
