mapper.delete:
delete from
T_ATTENDANCE
where TENANTID_ = #{tenantId}
and YEAR_ = #{year}
and MONTH_ = #{month}

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.YEAR_ desc, E.MONTH_ asc

mapper.sql:
from T_ATTENDANCE E


	1 = 1


		and E.TENANTID_ = #{tenantId}



		and E.TENANTID_ IN

			#{x_tenantId}




		and E.TYPE_ = #{type}



		and E.TYPE_ IN

			#{x_type}




		and E.YEAR_ = #{year}



		and E.YEAR_ >= #{yearGreaterThanOrEqual}



		and E.YEAR_ <= #{yearLessThanOrEqual}



		and E.YEAR_ IN

			#{x_year}




		and E.MONTH_ = #{month}



		and E.MONTH_ >= #{monthGreaterThanOrEqual}



		and E.MONTH_ <= #{monthLessThanOrEqual}



		and E.MONTH_ IN

			#{x_month}




		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}
