SELECT * FROM dual

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.JOINDATE_ desc

mapper.sql:
from T_EMPLOYEE E


	1 = 1


		and E.TENANTID_ = #{tenantId}



		and E.NAME_ like #{nameLike}



		and E.SEX_ = #{sex}



		and E.MOBILE_ like #{mobileLike}



		and E.NATION_ = #{nation}



		and E.MARRYSTATUS_ = #{marryStatus}



		and E.EDUCATION_ = #{education}



		and E.SENIORITY_ = #{seniority}



		and E.TYPE_ = #{type}



		and E.CATEGORY_ = #{category}



		and E.JOINDATE_ >= #{joinDateGreaterThanOrEqual}



		and E.JOINDATE_ <= #{joinDateLessThanOrEqual}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}



		and E.DELETEFLAG_ = #{deleteFlag}
